import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiSearch, FiClock, FiBookOpen, FiChevronRight } from 'react-icons/fi';

const AnimatedSearch = () => {
  const [searchPhase, setSearchPhase] = useState('initial'); // initial, typing, searching, results
  const [searchText, setSearchText] = useState('');
  const [showCursor, setShowCursor] = useState(true);
  const searchTextRef = useRef('');
  const searchPhrases = [
    'Curso de inteligencia artificial',
    'Aprender programación desde cero',
    'Curso avanzado de desarrollo web'
  ];
  const selectedPhrase = useRef(searchPhrases[0]);
  const phraseIndex = useRef(0);

  const courseResults = [
    {
      title: 'Fundamentos de Inteligencia Artificial',
      modules: 6,
      lessons: 12,
      duration: '3 horas',
      level: 'Principiante',
      topics: ['Machine Learning', 'Neural Networks', 'Data Processing']
    }
  ];

  // Efecto para la animación de escritura
  useEffect(() => {
    if (searchPhase === 'initial') {
      // Comenzar a escribir después de 1 segundo
      const timeout = setTimeout(() => {
        setSearchPhase('typing');
        setShowCursor(true);
      }, 1000);
      return () => clearTimeout(timeout);
    }

    if (searchPhase === 'typing') {
      // Simular escritura del texto
      if (searchTextRef.current.length < selectedPhrase.current.length) {
        const typingTimeout = setTimeout(() => {
          const nextChar = selectedPhrase.current[searchTextRef.current.length];
          searchTextRef.current += nextChar;
          setSearchText(searchTextRef.current);

          // Parpadeo del cursor
          const cursorTimeout = setTimeout(() => {
            setShowCursor(prev => !prev);
          }, 100);

          return () => clearTimeout(cursorTimeout);
        }, Math.random() * 100 + 50); // Velocidad de escritura variable

        return () => clearTimeout(typingTimeout);
      } else {
        // Finalizar la escritura y empezar a buscar
        const searchStartTimeout = setTimeout(() => {
          setSearchPhase('searching');
        }, 300);

        return () => clearTimeout(searchStartTimeout);
      }
    }

    if (searchPhase === 'searching') {
      // Simular proceso de búsqueda por 2 segundos
      const searchingTimeout = setTimeout(() => {
        setSearchPhase('results');
      }, 2000);

      return () => clearTimeout(searchingTimeout);
    }

    if (searchPhase === 'results') {
      // Mostrar resultados por 4 segundos y luego reiniciar
      const resetTimeout = setTimeout(() => {
        searchTextRef.current = '';
        setSearchText('');

        // Cambiar a la siguiente frase
        phraseIndex.current = (phraseIndex.current + 1) % searchPhrases.length;
        selectedPhrase.current = searchPhrases[phraseIndex.current];

        setSearchPhase('initial');
      }, 4000);

      return () => clearTimeout(resetTimeout);
    }
  }, [searchPhase, searchText]);

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="rounded-2xl bg-neutral-800/50 backdrop-blur-xl p-2 border border-neutral-700/60 shadow-xl overflow-hidden">
        {/* Barra de búsqueda */}
        <div className="flex items-center px-4 py-3 relative">
          <FiSearch className="text-neutral-400 mr-3 h-5 w-5" />

          <div className="flex-1 overflow-hidden">
            <div className="relative h-6 flex items-center">
              <div className="text-white text-base font-medium">
                {searchText}
                {showCursor && searchPhase !== 'results' && (
                  <span className="text-primary-400 opacity-70">|</span>
                )}
              </div>

              {searchPhase === 'searching' && (
                <motion.div 
                  className="ml-2" 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.3 }}
                >
                  <div className="flex gap-1">
                    <motion.div 
                      className="h-1.5 w-1.5 rounded-full bg-primary-400" 
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 0.6, repeat: Infinity, repeatType: 'loop' }}
                    />
                    <motion.div 
                      className="h-1.5 w-1.5 rounded-full bg-primary-400" 
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 0.6, repeat: Infinity, repeatType: 'loop', delay: 0.2 }}
                    />
                    <motion.div 
                      className="h-1.5 w-1.5 rounded-full bg-primary-400" 
                      animate={{ scale: [1, 1.2, 1] }}
                      transition={{ duration: 0.6, repeat: Infinity, repeatType: 'loop', delay: 0.4 }}
                    />
                  </div>
                </motion.div>
              )}
            </div>
          </div>
        </div>

        {/* Resultado de la búsqueda */}
        <AnimatePresence>
          {searchPhase === 'results' && (
            <motion.div 
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              transition={{ duration: 0.3 }}
              className="border-t border-neutral-700/60 mt-1 pt-3 px-4 pb-3"
            >
              <div className="text-sm text-neutral-400 mb-3">Cursos encontrados (1)</div>

              <motion.div 
                initial={{ y: 10, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.2 }}
                className="bg-neutral-800/80 rounded-xl p-4 hover:bg-neutral-800 transition-colors cursor-pointer"
              >
                <div className="flex items-start justify-between">
                  <div>
                    <h3 className="text-white font-semibold text-lg">{courseResults[0].title}</h3>
                    <p className="text-neutral-400 text-sm mt-1">{courseResults[0].level} · {courseResults[0].modules} módulos</p>
                  </div>
                  <div className="bg-primary-500/20 text-primary-400 text-xs rounded-lg px-2 py-1 flex items-center">
                    <FiClock className="mr-1" /> {courseResults[0].duration}
                  </div>
                </div>

                <div className="mt-3 flex flex-wrap gap-2">
                  {courseResults[0].topics.map((topic, idx) => (
                    <div key={idx} className="bg-neutral-700/30 text-neutral-300 text-xs rounded-lg px-2 py-1">
                      {topic}
                    </div>
                  ))}
                </div>

                <div className="mt-4 grid grid-cols-1 gap-2">
                  {[...Array(3)].map((_, idx) => (
                    <div key={idx} className="flex items-center text-sm">
                      <div className="w-5 h-5 rounded-full bg-primary-600/20 flex items-center justify-center text-primary-400 mr-2">
                        <FiBookOpen size={12} />
                      </div>
                      <div className="text-neutral-300 flex-1">
                        {idx === 0 ? 'Introducción a la IA' : idx === 1 ? 'Algoritmos básicos' : 'Procesamiento de datos'}
                      </div>
                      <div className="text-neutral-500 text-xs">
                        {idx === 0 ? '15 min' : idx === 1 ? '25 min' : '20 min'}
                      </div>
                    </div>
                  ))}
                </div>

                <div className="mt-4 pt-3 border-t border-neutral-700/30 flex justify-between items-center">
                  <div className="text-neutral-400 text-xs">12 lecciones en total</div>
                  <motion.button 
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="flex items-center text-primary-400 text-sm bg-primary-500/10 px-3 py-1 rounded-lg hover:bg-primary-500/20 transition-colors"
                  >
                    Ver curso
                    <FiChevronRight className="ml-1" />
                  </motion.button>
                </div>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default AnimatedSearch;
