import React, { createContext, useState, useEffect } from 'react';

export const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  // Verificar si hay un tema guardado en localStorage, si no, usar oscuro por defecto
  const [darkMode, setDarkMode] = useState(() => {
    const savedTheme = localStorage.getItem('theme');
    // Si no hay tema guardado o es 'dark', usar modo oscuro
    return savedTheme ? savedTheme === 'dark' : true;
  });

  // Cambiar entre tema oscuro y claro
  const toggleDarkMode = () => {
    setDarkMode(prevMode => !prevMode);
  };

  // Guardar el tema en localStorage cuando cambie
  useEffect(() => {
    const theme = darkMode ? 'dark' : 'light';
    localStorage.setItem('theme', theme);

    // Aplicar la clase al elemento html para que los estilos se apliquen globalmente
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <ThemeContext.Provider value={{ darkMode, toggleDarkMode }}>
      {children}
    </ThemeContext.Provider>
  );
};
