import React from 'react';
import { motion } from 'framer-motion';
import { FiSun, FiMoon } from 'react-icons/fi';
import { useTheme } from '../context/ThemeContext';

const ThemeToggle = ({ className = '' }) => {
  const { theme, toggleTheme } = useTheme();

  return (
    <motion.button
      onClick={toggleTheme}
      className={`p-2 rounded-full transition-colors ${className}`}
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      aria-label={theme === 'dark' ? 'Cambiar a tema claro' : 'Cambiar a tema oscuro'}
    >
      <div className="relative w-12 h-6 bg-neutral-700/50 dark:bg-neutral-800 rounded-full overflow-hidden border border-neutral-600/30">
        <motion.div 
          className="absolute top-0.5 bottom-0.5 w-5 h-5 rounded-full bg-white dark:bg-primary-400"
          animate={{ 
            x: theme === 'dark' ? 1 : 25,
            backgroundColor: theme === 'dark' ? '#38bdf8' : '#ffffff'
          }}
          transition={{ type: 'spring', stiffness: 300, damping: 25 }}
        />

        <div className="absolute inset-0 flex items-center justify-between px-1.5 text-xs">
          <FiSun className="text-yellow-300" size={12} />
          <FiMoon className="text-primary-300" size={12} />
        </div>
      </div>
    </motion.button>
  );
};

export default ThemeToggle;
