import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion, useAnimation } from 'framer-motion';
import { FiArrowRight, FiClock, FiBookOpen, FiSave } from 'react-icons/fi';

// Import our new components
import TextScramble from '../components/TextScramble';
import GlowingOrb from '../components/GlowingOrb';
import TextCycler from '../components/TextCycler';
import ShinyButton from '../components/ShinyButton';
import BackgroundGrid from '../components/BackgroundGrid';

// Animation variants
const fadeIn = {
  hidden: { opacity: 0 },
  visible: { opacity: 1 }
};

const slideInFromLeft = {
  hidden: { x: -60, opacity: 0 },
  visible: { x: 0, opacity: 1 }
};

const slideInFromRight = {
  hidden: { x: 60, opacity: 0 },
  visible: { x: 0, opacity: 1 }
};

const fadeInUp = {
  hidden: { y: 40, opacity: 0 },
  visible: { y: 0, opacity: 1 }
};

const staggerContainer = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2
    }
  }
};

const Landing = () => {
  const controls = useAnimation();

  useEffect(() => {
    controls.start("visible");
  }, [controls]);

  const learningTopics = [
    "Inteligencia Artificial",
    "Programación",
    "Marketing Digital",
    "Diseño UX/UI",
    "Machine Learning",
    "Desarrollo Web"
  ];

  return (
    <motion.div 
      initial="hidden" 
      animate="visible" 
      className="min-h-screen bg-neutral-900 text-white overflow-hidden relative"
    >
      {/* Background Grid */}
      <BackgroundGrid dotColor="rgba(80, 160, 255, 0.15)" dotSpacing={30} />

      {/* Header/Navigation */}
      <motion.header 
        variants={fadeIn}
        transition={{ duration: 0.5 }}
        className="bg-neutral-900/70 backdrop-blur-lg sticky top-0 z-10 border-b border-neutral-800"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16 items-center">
            <motion.div 
              variants={slideInFromLeft}
              transition={{ duration: 0.6 }}
              className="flex-shrink-0 flex items-center"
            >
              <h1 className="text-xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-primary-400 to-primary-600">
                <TextScramble text="Prompt2Course" delay={300} duration={1500} />
              </h1>
            </motion.div>
            <motion.div 
              variants={slideInFromRight}
              transition={{ duration: 0.6 }}
              className="flex items-center space-x-4"
            >
              <Link to="/login">
                <motion.button 
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="px-4 py-2 rounded-xl bg-transparent border border-neutral-700 text-neutral-300 hover:bg-neutral-800 transition-all"
                >
                  Iniciar sesión
                </motion.button>
              </Link>
              <Link to="/register">
                <ShinyButton primary>
                  Registrarse
                </ShinyButton>
              </Link>
            </motion.div>
          </div>
        </div>
      </motion.header>

      {/* Hero Section */}
      <section className="relative">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-16 pb-24 md:pt-24 md:pb-32">
          <div className="lg:grid lg:grid-cols-12 lg:gap-8">
            <div className="sm:text-center md:max-w-2xl md:mx-auto lg:col-span-6 lg:text-left">
              <motion.h1 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.7 }}
                className="text-4xl tracking-tight font-bold text-white sm:text-5xl md:text-6xl"
              >
                <motion.span 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.5, delay: 0.2 }}
                  className="block"
                >
                  Aprende cualquier
                </motion.span>

                {/* Contenedor con altura fija para el ciclo de texto */}
                <div className="h-20 mt-2 overflow-visible relative">
                  <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.5, delay: 0.5 }}
                  >
                    <TextCycler 
                      texts={learningTopics}
                      interval={2000}
                      textClassName="text-5xl md:text-6xl font-bold text-primary-400"
                    />
                  </motion.div>

                  {/* Efecto de glow para mayor visibilidad */}
                  <div className="absolute -inset-4 bg-primary-400/10 blur-xl rounded-full z-[-1]"></div>
                </div>
              </motion.h1>
              <motion.p 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.7 }}
                className="mt-6 text-lg text-neutral-300"
              >
                <TextScramble 
                  text="Prompt2Course te permite generar cursos personalizados para aprender cualquier tema, adaptados a tu nivel de experiencia y tiempo disponible."
                  delay={1000}
                  duration={1500}
                />
              </motion.p>
              <motion.div 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.9 }}
                className="mt-8 sm:flex sm:justify-center lg:justify-start"
              >
                <Link to="/register">
                  <ShinyButton className="py-3 px-6 text-base flex items-center space-x-2">
                    <span>Comenzar gratis</span>
                    <FiArrowRight className="ml-2" />
                  </ShinyButton>
                </Link>
                <motion.div 
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className="mt-3 sm:mt-0 sm:ml-3"
                >
                  <Link
                    to="/login"
                    className="inline-flex items-center px-6 py-3 border border-neutral-700 rounded-xl text-base font-medium text-neutral-300 bg-neutral-900 hover:bg-neutral-800 transition-all"
                  >
                    Iniciar sesión
                  </Link>
                </motion.div>
              </motion.div>
            </div>
            <div className="mt-12 relative sm:max-w-lg sm:mx-auto lg:mt-0 lg:max-w-none lg:mx-0 lg:col-span-6 lg:flex lg:items-center justify-center">
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.8, delay: 0.3 }}
                className="relative w-full max-w-lg"
              >
                {/* Glowing orb background effect */}
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-0">
                  <GlowingOrb size={400} color="primary" intensity={1.2} />
                </div>

                {/* Main content card */}
                <motion.div
                  initial={{ y: 30, opacity: 0, scale: 0.9 }}
                  animate={{ y: 0, opacity: 1, scale: 1 }}
                  transition={{ 
                    duration: 0.8, 
                    delay: 0.7,
                    type: "spring",
                    stiffness: 100
                  }}
                  className="relative z-10 bg-neutral-800/50 backdrop-blur-xl rounded-2xl shadow-2xl overflow-hidden border border-neutral-700"
                >
                  <div className="relative p-6">
                    {/* Image */}
                    <motion.div
                      initial={{ y: 30, opacity: 0 }}
                      animate={{ y: 0, opacity: 1 }}
                      transition={{ duration: 0.8, delay: 0.8 }}
                      className="relative aspect-video w-full flex items-center justify-center rounded-xl overflow-hidden"
                    >
                      <img
                        src="/image.png"
                        alt="Prompt2Course AI Learning"
                        className="w-full h-full object-cover rounded-xl"
                      />

                      {/* Modern gradient overlay */}
                      <div className="absolute inset-0 bg-gradient-to-r from-primary-700/30 to-primary-900/30 rounded-xl" />
                    </motion.div>

                    {/* Modern UI elements */}
                    <div className="mt-4">
                      <h3 className="text-lg font-semibold text-white">Curso generado con IA</h3>
                      <p className="text-neutral-300 text-sm mt-1">Aprende a tu propio ritmo</p>

                      <div className="mt-4 flex items-center justify-between">
                        <div className="flex items-center space-x-2">
                          <span className="inline-flex h-2 w-2 rounded-full bg-primary-500"></span>
                          <span className="text-xs text-neutral-400">En vivo</span>
                        </div>

                        <motion.button 
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          className="text-xs px-2 py-1 rounded-lg bg-primary-600/20 text-primary-400 border border-primary-700/30"
                        >
                          Vista previa
                        </motion.button>
                      </div>
                    </div>
                  </div>
                </motion.div>

                {/* Floating elements for modern UI feel */}
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 1.2, duration: 0.6 }}
                  className="absolute -left-10 top-1/4 z-20 bg-neutral-800/70 backdrop-blur-md p-3 rounded-xl border border-neutral-700 shadow-lg"
                >
                  <div className="flex items-center space-x-2">
                    <div className="h-8 w-8 rounded-full bg-primary-500 flex items-center justify-center text-white">
                      <FiBookOpen size={16} />
                    </div>
                    <span className="text-sm font-medium">12 lecciones</span>
                  </div>
                </motion.div>

                <motion.div
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 1.4, duration: 0.6 }}
                  className="absolute -right-10 bottom-1/4 z-20 bg-neutral-800/70 backdrop-blur-md p-3 rounded-xl border border-neutral-700 shadow-lg"
                >
                  <div className="flex items-center space-x-2">
                    <div className="h-8 w-8 rounded-full bg-primary-500 flex items-center justify-center text-white">
                      <FiClock size={16} />
                    </div>
                    <span className="text-sm font-medium">3 horas</span>
                  </div>
                </motion.div>
              </motion.div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <motion.section 
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.2 }}
        variants={fadeIn}
        transition={{ duration: 0.5 }}
        className="bg-neutral-900 relative py-16 sm:py-24 overflow-hidden"
      >
        {/* Subtle background effect */}
        <div className="absolute inset-0 overflow-hidden">
          <div className="absolute -top-40 -right-40 w-80 h-80 bg-primary-500/5 rounded-full blur-3xl" />
          <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-primary-600/5 rounded-full blur-3xl" />
        </div>

        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
          <motion.div 
            variants={fadeInUp}
            transition={{ duration: 0.6 }}
            className="lg:text-center"
          >
            <motion.h2 
              variants={fadeInUp}
              className="text-base text-primary-400 font-semibold tracking-wide uppercase"
            >
              <TextScramble text="Características" delay={200} duration={1000} />
            </motion.h2>
            <motion.p 
              variants={fadeInUp}
              className="mt-2 text-3xl leading-8 font-bold tracking-tight text-white sm:text-4xl"
            >
              Todo lo que necesitas para aprender eficientemente
            </motion.p>
            <motion.p 
              variants={fadeInUp}
              className="mt-4 max-w-2xl text-xl text-neutral-300 lg:mx-auto"
            >
              Prompt2Course simplifica el proceso de aprendizaje con herramientas intuitivas y recursos personalizados.
            </motion.p>
          </motion.div>

          <motion.div 
            variants={staggerContainer}
            transition={{ duration: 0.3, delayChildren: 0.3, staggerChildren: 0.2 }}
            className="mt-16"
          >
            <div className="grid grid-cols-1 gap-8 sm:grid-cols-2 lg:grid-cols-3">
              {/* Feature 1 */}
              <motion.div 
                variants={fadeInUp}
                whileHover={{ y: -10, transition: { duration: 0.2 } }}
                className="bg-neutral-800/50 backdrop-blur-sm rounded-2xl p-8 border border-neutral-700/50 shadow-xl"
              >
                <div>
                  <motion.div 
                    whileHover={{ scale: 1.1, rotate: 5 }}
                    transition={{ type: "spring", stiffness: 300 }}
                    className="flex items-center justify-center h-12 w-12 rounded-full bg-gradient-to-br from-primary-400 to-primary-600 text-white"
                  >
                    <FiBookOpen className="h-6 w-6" />
                  </motion.div>
                  <div className="mt-5">
                    <h3 className="text-xl font-medium text-white">Cursos personalizados</h3>
                    <p className="mt-2 text-base text-neutral-300">
                      Genera cursos adaptados a tus necesidades específicas, nivel de experiencia y objetivo de aprendizaje.
                    </p>
                  </div>
                </div>
              </motion.div>

              {/* Feature 2 */}
              <motion.div 
                variants={fadeInUp}
                whileHover={{ y: -10, transition: { duration: 0.2 } }}
                className="bg-neutral-800/50 backdrop-blur-sm rounded-2xl p-8 border border-neutral-700/50 shadow-xl"
              >
                <div>
                  <motion.div 
